<?php//SMTP needs accurate times, and the PHP time zone MUST be set//This should be done in your php.ini, but this is how to do it if you don't have access to thatdate_default_timezone_set('Etc/UTC');use PHPMailer\PHPMailer\PHPMailer;use PHPMailer\PHPMailer\Exception;//Load Composer's autoloaderrequire  __DIR__ . '/../vendor/autoload.php';class Mailer{	var $mail;	public function __construct() {		$this->mail = new PHPMailer;		$this->mail->isSMTP();		//Enable SMTP debugging		// 0 = off (for production use)		// 1 = client messages		// 2 = client and server messages		// 4 		$this->mail->SMTPDebug = 5;		//Ask for HTML-friendly debug output		$this->mail->Debugoutput = 'error_log';		//Set the hostname of the mail server		$this->mail->Host = gethostbyname("tls://mail.cardition.com");		//Set the SMTP port number - likely to be 25, 465 or 587		$this->mail->Port = 25;		//Whether to use SMTP authentication		$this->mail->SMTPAuth = true;		//Username to use for SMTP authentication		$this->mail->Username = "info@cardition.com";		//Password to use for SMTP authentication		$this->mail->Password = "password01";		$this->mail->IsHTML(true);	}	public function send_mail($from,$to,$subject,$body){		$this->mail->Subject = $subject;		$this->mail->setFrom($this->mail->Username, "Cardition");		$this->mail->addAddress($to, "");		$this->mail->Body = $body;		$success = $this->mail->send();		if (!$success) {		    return array('state' => 'error','error_info' => $this->mail->ErrorInfo);		} else {		    return array('state' => 'success');		}	}}